package for_loop;

public class SumOfNatualNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int num = 10,sum = 0;
for (int i = 1; i <= num; i++)
{
	sum = sum + i;
}
System.out.println("sum " +sum);
{
	for (int i = 10; i > 0; i--) {
		System.out.println(i);
}
}
}
}