package for_loop;

public class UppercaseAlphabet {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
char c;
for(c = 'A'; c <= 'Z'; c = ++c) {
	System.out.print(c + " ");
}
System.out.println("************************");
for(c = 'a'; c <= 'z'; c = ++c) {
	System.out.print(c + " ");
	}
	}
}
