package if_else;

public class LargestNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
double P1 =-4.5, P2 = 3.9, P3 = 2.5;
if(P1 >= P2 && P1 >= P3)
System.out.println("The Largest Number is = " +P1);
else if (P2 >= P3 && P2 >= P3)
	System.out.println("The Largest Number is = " +P2);
else
	System.out.println("The Largest Number is = " +P3);
	
	}

}
